import React, { useState } from 'react';
import './search.css';

function Search({Option}) {
    const [searchText, setsearchText] = useState("");
    const [course,setCourse] = useState('');
    const [diet,setDiet] = useState('');
    const [cuisines,setCuisines] = useState('');

    const optionElement=(options,state,setState,id)=>{
        return(
            <select key={id} value={state} onChange={(event)=>setState(event.target.value)}>
            <option></option>
            {options.map((value,index)=>{
                return(<option key={index} value={value}>{value}</option>)
            })}
          </select>
        );
    }

    return(
        <div className='container'>
            {Option && Option.diet && optionElement(Option.diet, diet , setDiet ,1)}
            {Option && Option.course && optionElement(Option.course, course , setCourse ,2)}
            {Option && Option.cuisines && optionElement(Option.cuisines, cuisines , setCuisines ,3)}
            <input className='search' type="text" onChange={(event)=>setsearchText(event.target.value)} value={searchText} placeholder="Search keywords"></input>
        </div>
    );
}

export default Search;
